mapper.select:
SELECT e.*, d.name as deptName 
FROM emp e 
LEFT JOIN dept d ON e.dept_id = d.id


        AND e.name LIKE CONCAT('%', #{name}, '%')


        AND e.gender = #{gender}


        AND e.hire_date >= #{begin}


        AND e.hire_date <= #{end}


ORDER BY e.update_time DESC

mapper.update:
UPDATE emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, phone = #{phone}, position = #{position}, salary = #{salary}, image = #{image}, hire_date = #{hireDate}, dept_id = #{deptId}
WHERE id = #{id}
